var React = require('react');

var MODALIZE_CONTAINER_ID = 'modalize-container';

function _getContainer() {
  var modalizeContainer = document.getElementById(MODALIZE_CONTAINER_ID);
  if (!modalizeContainer) {
    modalizeContainer = document.createElement('div');
    modalizeContainer.setAttribute('id', MODALIZE_CONTAINER_ID);
    document.body.appendChild(modalizeContainer);
  }
  return modalizeContainer;
}


module.exports.show = function show(Component, props, container) {
  container = _getContainer();
  React.render(<Component {...props}/>,
    container);
};